listeners.loadcontent:
function(field) {
	var tab = field.findParentByType('panel'); //find the dialog
	var container=tab.findByType('fieldset')[0];
    /*hide or show component based on checked value */
	if(field.getValue() == 'true')
	{
	container.setVisible(true)
	}
	else{		
	container.setVisible(false);
	}
}

listeners.selectionchanged:
function(comp, val, isChecked) {
    var tab = comp.findParentByType('panel'); //find the tab
	var container=tab.findByType('fieldset')[0];
    /*hide or show component based on checked value */
    isChecked ? container.setVisible(true): container.setVisible(false);
}

